/**
 * Helper function miniTPL(obj, tag, data)
 * @author [29-03-2014 11:38 Alfredo Gonzalez P. <alfredo@internoma.com>]
 * @param  {String} obj (name for object)
 * @param  {String} tag (template tag target)
 * @param  {String} data (data-attribute)
 * @example:
 *     <tpl data-bind="key" />
 *     <tpl data-bind="key1" />
 *
 *     miniTPL({'key':'value', 'key1': 'value1'});
 * @optional:
 *     // initialize/clear all template tags
 *     miniTPL('delete');
 */function miniTPL(e,t,n){t=t||"tpl";n=n||"data-bind";var r=document.getElementsByTagName(t);if(e==="delete")for(var i=0;i<r.length;i++)r[i].textContent=null;else{var s;for(var o=0;o<r.length;o++){s=r[o].getAttribute(n);e[s]&&(r[o].textContent=e[s])}}};